use itertools::Itertools;

pub fn part_1() {
    let input = "";
    let packet_marker_length = 4;

    for idx in 0..(input.len() - packet_marker_length + 1) {
        let current_packet: Vec<char> = input[idx..(idx + packet_marker_length)].chars().collect();
        let current_packet_unique: Vec<char> = current_packet.into_iter().unique().collect();
        if current_packet_unique.len() == packet_marker_length {
            println!("{:?}", idx + packet_marker_length);
            break;
        }
    }
}

